import React, { useEffect, useState } from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";
import Auth from "./Auth";
import Restaurants from "./Restaurants";

function App() {
  const shouldRedirect = set;
  const navigate = useNavigate();

  const onSignup = (e: any) => {
    e.preventDefault();
    // alert("signup");
    navigate("/restaraunts");
  };

  const onLogin = (e: any) => {
    e.preventDefault();
    // alert("login");
    shouldRedirect = true;
  };

  React.useEffect(() => {
    if (shouldRedirect) {
      navigate("/restaurants");
    }
  });

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/login"
          element={<Auth onSignup={onSignup} onLogin={onLogin} />}
        />
        <Route path="/restaraunts" element={<Restaurants />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
